import React, { useState, useEffect } from 'react';
import Header from "../sections/Header";
import Footer from "../sections/Footer";
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Profiler = () => {
  const [userInfo, setUserInfo] = useState({
    imgUrl: 'https://static.vecteezy.com/system/resources/thumbnails/013/360/247/small/default-avatar-photo-icon-social-media-profile-sign-symbol-vector.jpg',
    user: '',
    gender: '',
    email: ''
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUserData = async () => {
      const username = localStorage.getItem('username');
      console.log("Retrieved username from storage:", username);

      if (!username) {
        console.log("No username found in storage, redirecting to login");
        setError('Please log in to view your profile');
        setLoading(false);
        navigate('/login');
        return;
      }

      try {
        console.log("Fetching profile data for:", username);
        const response = await axios.get(`http://localhost:3000/getUserProfile/${username}`);
        console.log("Profile data received:", response.data);
        
        if (response.data) {
          setUserInfo(response.data);
          setLoading(false);
        }
      } catch (error) {
        console.error("Error fetching profile:", error.response || error);
        setError(error.response?.data?.message || 'Failed to load user profile');
        setLoading(false);
        
        // If we get a 404, the user might not be logged in properly
        if (error.response?.status === 404) {
          localStorage.removeItem('username');
          navigate('/login');
        }
      }
    };

    fetchUserData();
  }, [navigate]);

  if (loading) {
    return (
      <div className="main-contact">
        <Header />
        <div className="container mt-5 text-center">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
          <p>Loading your profile...</p>
        </div>
        <Footer />
      </div>
    );
  }

  if (error) {
    return (
      <div className="main-contact">
        <Header />
        <div className="container mt-5">
          <div className="alert alert-danger text-center" role="alert">
            {error}
          </div>
        </div>
        <Footer />
      </div>
    );
  }

  return (
    <div className="main-contact">
      <Header />
      <div className="container my-5 d-flex justify-content-center" style={{ paddingTop: "100px" }}>
        <div className="card p-4 shadow" style={{ maxWidth: "600px", width: "100%" }}>
          <form>
            <h2 className="text-center mb-4">User Profile</h2>
            <div className="d-flex align-items-center gap-4 mb-4">
              <div className="flex-shrink-0">
                <img
                  src={userInfo.imgUrl || 'https://static.vecteezy.com/system/resources/thumbnails/013/360/247/small/default-avatar-photo-icon-social-media-profile-sign-symbol-vector.jpg'}
                  alt="Profile"
                  width="120"
                  height="120"
                  className="rounded-circle border"
                />
              </div>
              <div className="w-100">
                <div className="mb-3">
                  <label className="form-label"><strong>Name: </strong></label>
                  <input type="text" className="form-control" value={userInfo.user || ''} readOnly />
                </div>
                <div className="mb-3">
                  <label className="form-label"><strong>Email: </strong></label>
                  <input type="email" className="form-control" value={userInfo.email || ''} readOnly />
                </div>
                <div className="mb-3">
                  <label className="form-label"><strong>Gender: </strong></label>
                  <input type="text" className="form-control" value={userInfo.gender || ''} readOnly />
                </div>
              </div>
            </div>
          </form>
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default Profiler;
