import React, { useState, useEffect } from 'react';
import Header from "../sections/Header";
import Footer from "../sections/Footer";
import { useNavigate } from 'react-router-dom';
import axios from 'axios';


const Profiler = () => {
  const [userInfo, setUserInfo] = useState({
    imgUrl: 'https://static.vecteezy.com/system/resources/thumbnails/013/360/247/small/default-avatar-photo-icon-social-media-profile-sign-symbol-vector.jpg',
    user: 'John Doe',
    gender: 'Male',
    email: 'John@gmail.com'
  });

  const [isEditing, setIsEditing] = useState(false);
  const [editForm, setEditForm] = useState({
    username: '',
    email: ''
  });
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState({ text: '', type: '' });

  const navigate = useNavigate();

  const ResetPassword = () => {
    navigate('/verifyEmail');
  };

  // Handle edit mode toggle
  const handleEditToggle = () => {
    if (!isEditing) {
      // Initialize edit form with current values
      setEditForm({
        username: userInfo.user,
        email: userInfo.email
      });
    }
    setIsEditing(!isEditing);
    setMessage({ text: '', type: '' });
  };

  // Handle form input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditForm(prev => ({
      ...prev,
      [name]: value
    }));
  };

  // Handle image file upload
  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (event) => {
        setEditForm(prev => ({
          ...prev,
          imgUrl: event.target.result
        }));
      };
      reader.readAsDataURL(file);
    }
  };

  // Validate form
  const validateForm = () => {
    // Username validation
    if (!editForm.username || editForm.username.trim().length < 3) {
      setMessage({ text: 'Username must be at least 3 characters long', type: 'error' });
      return false;
    }

    // Email validation
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!editForm.email || !emailRegex.test(editForm.email)) {
      setMessage({ text: 'Please enter a valid email address', type: 'error' });
      return false;
    }

    // Password validation
    if (editForm.password && editForm.password !== editForm.confirmPassword) {
      setMessage({ text: 'Passwords do not match', type: 'error' });
      return false;
    }
    if (editForm.password && editForm.password.length < 6) {
      setMessage({ text: 'Password must be at least 6 characters long', type: 'error' });
      return false;
    }

    return true;
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) {
      return;
    }

    setLoading(true);
    setMessage({ text: '', type: '' });

    try {
      const username = localStorage.getItem('username');
      const updateData = {
        gender: editForm.gender,
        imgUrl: editForm.imgUrl,
        newUsername: editForm.username !== userInfo.user ? editForm.username : undefined,
        email: editForm.email !== userInfo.email ? editForm.email : undefined
      };

      // Only include password if it's provided
      if (editForm.password) {
        updateData.password = editForm.password;
      }

      const response = await axios.put(`http://localhost:5000/updateUser/${username}`, updateData);
      
      if (response.data.message === 'User updated successfully.') {
        // Update local state with new data
        setUserInfo(prev => ({
          ...prev,
          user: editForm.username,
          email: editForm.email,
          gender: editForm.gender,
          imgUrl: editForm.imgUrl
        }));
        
        // Update localStorage if username changed
        if (editForm.username !== userInfo.user) {
          localStorage.setItem('username', editForm.username.toLowerCase());
        }
        
        setMessage({ text: 'Profile updated successfully!', type: 'success' });
        setIsEditing(false);
        
        // Clear password fields
        setEditForm(prev => ({
          ...prev,
          password: '',
          confirmPassword: ''
        }));
      }
    } catch (error) {
      console.error('Error updating profile:', error);
      setMessage({ 
        text: error.response?.data?.message || 'Failed to update profile. Please try again.', 
        type: 'error' 
      });
    } finally {
      setLoading(false);
    }
  };

  // Handle cancel edit
  const handleCancel = () => {
    setIsEditing(false);
    setEditForm({
      username: '',
      email: '',
      gender: '',
      imgUrl: '',
      password: '',
      confirmPassword: ''
    });
    setMessage({ text: '', type: '' });
  };

  useEffect(() => {
    const fetchUserData = async () => {
      const username = localStorage.getItem('username');
      if (username) {
        try {
          const response = await axios.get(`http://localhost:5000/getUserProfile/${username}`);
          console.log("User profile data:", response.data);
          if (response.data) {
            // Handle different image URL formats
            let fullImgUrl = "https://static.vecteezy.com/system/resources/thumbnails/013/360/247/small/default-avatar-photo-icon-social-media-profile-sign-symbol-vector.jpg";
            
            if (response.data.imgUrl) {
              // If it's already a full URL, use it directly
              if (response.data.imgUrl.startsWith('http')) {
                fullImgUrl = response.data.imgUrl;
              } else {
                // If it's a relative path, construct the full URL
                fullImgUrl = `http://localhost:5000/${response.data.imgUrl}`;
              }
            }

            setUserInfo(prev => ({
              ...prev,
              ...response.data,
              imgUrl: fullImgUrl
            }));
          }
        } catch (error) {
          console.error('Error fetching user data:', error);
          // Keep default image on error
        }
      }
    };
    fetchUserData();
  }, []);

  return (
    <div className="main-contact">
      <Header />
      <div className="container my-5 d-flex justify-content-center" style={{ paddingTop: "100px" }}>
        <div className="card p-4 shadow" style={{ maxWidth: "600px", width: "100%" }}>
          <form onSubmit={handleSubmit}>
            <div className="d-flex justify-content-between align-items-center mb-4">
              <h2 className="text-center mb-0">User Profile</h2>
              <button 
                type="button" 
                className={`btn ${isEditing ? 'btn-secondary' : 'btn-primary'}`}
                onClick={handleEditToggle}
                disabled={loading}
              >
                {isEditing ? 'Cancel' : 'Edit Profile'}
              </button>
            </div>

            {/* Message display */}
            {message.text && (
              <div className={`alert ${message.type === 'success' ? 'alert-success' : 'alert-danger'} mb-3`}>
                {message.text}
              </div>
            )}

            <div className="d-flex align-items-center gap-4 mb-4">
              <div className="flex-shrink-0">
                <img
                  src={isEditing ? editForm.imgUrl || userInfo.imgUrl : userInfo.imgUrl}
                  alt="Profile"
                  width="120"
                  height="120"
                  className="rounded-circle border"
                  onError={(e) => {
                    console.log('Image failed to load, using default');
                    e.target.src = "https://static.vecteezy.com/system/resources/thumbnails/013/360/247/small/default-avatar-photo-icon-social-media-profile-sign-symbol-vector.jpg";
                  }}
                  style={{ objectFit: 'cover' }}
                />
                
                {/* Image upload input - only show in edit mode */}
                {isEditing && (
                  <div className="mt-2">
                    <input
                      type="file"
                      accept="image/*"
                      onChange={handleImageUpload}
                      className="form-control form-control-sm"
                      id="imageUpload"
                    />
                    <label htmlFor="imageUpload" className="form-label small text-muted">
                      Upload new profile picture
                    </label>
                  </div>
                )}
              </div>
              
              <div className="w-100">
                <div className="mb-3">
                  <label className="form-label"><strong>Username: </strong></label>
                  {isEditing ? (
                    <input
                      type="text"
                      name="username"
                      value={editForm.username}
                      onChange={handleInputChange}
                      className="form-control"
                      placeholder="Enter username"
                      required
                    />
                  ) : (
                    <input 
                      type="text" 
                      className="form-control" 
                      value={userInfo.user} 
                      readOnly 
                    />
                  )}
                </div>
                
                <div className="mb-3">
                  <label className="form-label"><strong>Email: </strong></label>
                  {isEditing ? (
                    <input
                      type="email"
                      name="email"
                      value={editForm.email}
                      onChange={handleInputChange}
                      className="form-control"
                      placeholder="Enter email"
                      required
                    />
                  ) : (
                    <input 
                      type="email" 
                      className="form-control" 
                      value={userInfo.email} 
                      readOnly 
                    />
                  )}
                </div>
                
                <div className="mb-3">
                  <label className="form-label"><strong>Gender: </strong></label>
                  {isEditing ? (
                    <select
                      name="gender"
                      value={editForm.gender}
                      onChange={handleInputChange}
                      className="form-control"
                    >
                      <option value="">Select Gender</option>
                      <option value="Male">Male</option>
                      <option value="Female">Female</option>
                      <option value="Other">Other</option>
                    </select>
                  ) : (
                    <input 
                      type="text" 
                      className="form-control" 
                      value={userInfo.gender} 
                      readOnly 
                    />
                  )}
                </div>
              </div>
            </div>

            {/* Password change section - only show in edit mode */}
            {isEditing && (
              <div className="mb-4">
                <h5>Change Password (Optional)</h5>
                <div className="row">
                  <div className="col-md-6">
                    <div className="mb-3">
                      <label className="form-label">New Password</label>
                      <input
                        type="password"
                        name="password"
                        value={editForm.password}
                        onChange={handleInputChange}
                        className="form-control"
                        placeholder="Enter new password"
                      />
                    </div>
                  </div>
                  <div className="col-md-6">
                    <div className="mb-3">
                      <label className="form-label">Confirm Password</label>
                      <input
                        type="password"
                        name="confirmPassword"
                        value={editForm.confirmPassword}
                        onChange={handleInputChange}
                        className="form-control"
                        placeholder="Confirm new password"
                      />
                    </div>
                  </div>
                </div>
              </div>
            )}

            {/* Action buttons */}
            <div className="d-flex justify-content-between">
              <button 
                type="button" 
                className="btn btn-link text-decoration-none" 
                onClick={ResetPassword}
              >
                Reset password?
              </button>
              
              {isEditing && (
                <div>
                  <button 
                    type="button" 
                    className="btn btn-secondary me-2" 
                    onClick={handleCancel}
                    disabled={loading}
                  >
                    Cancel
                  </button>
                  <button 
                    type="submit" 
                    className="btn btn-primary"
                    disabled={loading}
                  >
                    {loading ? 'Updating...' : 'Save Changes'}
                  </button>
                </div>
              )}
            </div>
          </form>
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default Profiler;
