import React, { useContext } from 'react';
import '../css/Home.css';
import Header from '../sections/Header';
import logo from '../assets/logoPNG.PNG';
import Footer from '../sections/Footer';
import {Row,Col} from 'reactstrap';
import ApplianceCards from '../sections/AppliancesCatalog';
import { DarkModeContext } from '../sections/DarkModeContext';

const Home = () => {
  const { darkMode } = useContext(DarkModeContext);

  return (
    <div 
      className={`container1 ${darkMode ? 'bg-dark text-light' : 'bg-light text-dark'}`}
      style={darkMode ? { 
        backgroundImage: 'none', 
        backgroundColor: '#1a1a1a',
        color: '#ffffff'
      } : {
        backgroundColor: '#ffffff'
      }}
    >
      <Header />
      <section className="header" style={{ backgroundColor: darkMode ? '#2d2d2d' : '#7B4F2C' }}>
        <div className="container1">
          <h1 
            className="brown-text" 
            style={{ 
              color: darkMode ? '#ffffff' : '#7B4F2C',
              textShadow: darkMode ? '2px 2px 4px rgba(0,0,0,0.8)' : 'none'
            }}
          >
            Renting Household Appliances
          </h1>
          <br/>
          <img 
            src={logo} 
            alt="Profile"  
            width="400px" 
            height="400px"
            style={{
              filter: darkMode ? 'brightness(0.8) contrast(1.2)' : 'none',
              borderRadius: darkMode ? '20px' : '0px',
              boxShadow: darkMode ? '0 8px 32px rgba(0,0,0,0.5)' : 'none'
            }}
          />
        </div>
      </section>
      <div className="content-row" style={{ backgroundColor: darkMode ? 'rgba(0,0,0,0.3)' : 'transparent' }}>
        <div className="column1">
        </div>
        <div className="column2">
        </div>
        <Row className="justify-content-center">
        <Col xs="12" md="8" className="px-md-5 px-3">
            <ApplianceCards />
          </Col>
        </Row>
      </div>
      <Footer />
    </div>
  );
};

export default Home;
