import React from 'react';
import { useSelector } from 'react-redux';
import Header from "../sections/Header";
import Footer from "../sections/Footer";
import { useNavigate } from 'react-router-dom';

const Profiler = () => {
  const imgUrl = useSelector((state) => state.users.user.imgUrl);
  const username = useSelector((state) => state.users.user.user);
  const gender = useSelector((state) => state.users.user.gender);
  const email = useSelector((state) => state.users.user.email);
  const defimg = 'https://static.vecteezy.com/system/resources/thumbnails/013/360/247/small/default-avatar-photo-icon-social-media-profile-sign-symbol-vector.jpg';
  const navigate = useNavigate();

  const handelReset = () => {
    navigate('/reset-password')
  }

  return (
    <div className="main-contact">
      <Header />
      <div className="container my-5 d-flex justify-content-center" style={{ paddingTop: "100px" }}>
      <div className="card p-4 shadow" style={{ maxWidth: "600px", width: "100%" }}>
          <form>
            <h2 className="text-center mb-4">User Profile</h2>
            <div className="d-flex align-items-center gap-4 mb-4">
              <div className="flex-shrink-0">
                <img
                  src={imgUrl ? imgUrl : defimg}
                  alt="Profile"
                  width="120"
                  height="120"
                  className="rounded-circle border"
                />
              </div>
              <div className="w-100">
                <div className="mb-3">
                  <label className="form-label"><strong>Name: </strong></label>
                  <input type="text" className="form-control" value={username} readOnly />
                </div>
                <div className="mb-3">
                  <label className="form-label"><strong>Email: </strong></label>
                  <input type="email" className="form-control" value={email} readOnly />
                </div>
                <div className="mb-3">
                  <label className="form-label"><strong>Gender: </strong></label>
                  <input type="text" className="form-control" value={gender} readOnly />
                    <button className="tag-button right" onClick={handelReset}>Reset Password</button>
                </div>
              </div>
            </div>
          </form>
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default Profiler;
