import React, { useState, useEffect } from 'react';
import Header from "../sections/Header";
import Footer from "../sections/Footer";
import axios from 'axios';

const Profiler = () => {
  const [userData, setUserData] = useState({
    imgUrl: 'https://static.vecteezy.com/system/resources/thumbnails/013/360/247/small/default-avatar-photo-icon-social-media-profile-sign-symbol-vector.jpg',
    user: '',
    gender: '',
    email: ''
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    // Get the username from localStorage or wherever you store it after login
    const username = localStorage.getItem('username');
    if (username) {
      axios.get(`http://localhost:3000/getUserProfile/${username}`)
        .then(response => {
          setUserData(response.data);
          setLoading(false);
        })
        .catch(error => {
          console.error('Error fetching user data:', error);
          setError('Failed to load user profile');
          setLoading(false);
        });
    } else {
      setError('User not logged in');
      setLoading(false);
    }
  }, []);

  if (loading) {
    return <div className="text-center mt-5">Loading...</div>;
  }

  if (error) {
    return <div className="text-center mt-5 text-danger">{error}</div>;
  }

  return (
    <div className="main-contact">
      <Header />
      <div className="container my-5 d-flex justify-content-center" style={{ paddingTop: "100px" }}>
        <div className="card p-4 shadow" style={{ maxWidth: "600px", width: "100%" }}>
          <form>
            <h2 className="text-center mb-4">User Profile</h2>
            <div className="d-flex align-items-center gap-4 mb-4">
              <div className="flex-shrink-0">
                <img
                  src={userData.imgUrl || 'https://static.vecteezy.com/system/resources/thumbnails/013/360/247/small/default-avatar-photo-icon-social-media-profile-sign-symbol-vector.jpg'}
                  alt="Profile"
                  width="120"
                  height="120"
                  className="rounded-circle border"
                />
              </div>
              <div className="w-100">
                <div className="mb-3">
                  <label className="form-label"><strong>Name: </strong></label>
                  <input type="text" className="form-control" value={userData.user} readOnly />
                </div>
                <div className="mb-3">
                  <label className="form-label"><strong>Email: </strong></label>
                  <input type="email" className="form-control" value={userData.email} readOnly />
                </div>
                <div className="mb-3">
                  <label className="form-label"><strong>Gender: </strong></label>
                  <input type="text" className="form-control" value={userData.gender} readOnly />
                </div>
              </div>
            </div>
          </form>
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default Profiler;
