import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  Card,
  CardBody,
  CardTitle,
  CardImg,
  Button,
  Spinner,
  CardText,
  Row,
  Col,
  Input,
  InputGroup,
  InputGroupText
} from 'reactstrap';
import { useNavigate } from 'react-router-dom';

const ApplianceCards = () => {
  const [appliances, setAppliances] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [suggestions, setSuggestions] = useState([]);
  const navigate = useNavigate();

  // Function to fetch appliances
  const fetchAppliances = async () => {
    try {
      const response = await axios.get('http://localhost:3000/getSpecificAppliance');
      setAppliances(response.data.Appliance || []);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching appliances:', error);
      setLoading(false);
    }
  };

  const handelBooking = () => {
    navigate("/Rental");
  };

  // Get appliances on mount
  useEffect(() => {
    fetchAppliances();
  }, []);

  // Listen for storage events to refresh data when appliances are updated
  useEffect(() => {
    const handleStorageChange = (e) => {
      if (e.key === 'applianceUpdated') {
        console.log('Appliance updated, refreshing data...');
        fetchAppliances();
        localStorage.removeItem('applianceUpdated');
      }
    };

    // Also listen for custom events
    const handleApplianceUpdate = () => {
      console.log('Appliance update event received, refreshing data...');
      fetchAppliances();
    };

    window.addEventListener('storage', handleStorageChange);
    window.addEventListener('applianceUpdated', handleApplianceUpdate);

    return () => {
      window.removeEventListener('storage', handleStorageChange);
      window.removeEventListener('applianceUpdated', handleApplianceUpdate);
    };
  }, []);

  // Poll for updates every 30 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      const lastUpdate = localStorage.getItem('applianceLastUpdate');
      if (lastUpdate) {
        const now = Date.now();
        const timeDiff = now - parseInt(lastUpdate);
        if (timeDiff < 60000) { // If updated within last minute
          console.log('Polling detected recent update, refreshing...');
          fetchAppliances();
          localStorage.removeItem('applianceLastUpdate');
        }
      }
    }, 30000); // Check every 30 seconds

    return () => clearInterval(interval);
  }, []);

  // Fetch suggestions when searchTerm changes
  useEffect(() => {
    if (searchTerm.trim() === '') {
      setSuggestions([]);
      return;
    }

    const fetchSuggestions = async () => {
      try {
        const res = await axios.get(`http://localhost:3000/api/suggestions?keyword=${searchTerm}`);
        setSuggestions(res.data);
      } catch (error) {
        console.error('Error fetching suggestions:', error);
      }
    };

    fetchSuggestions();
  }, [searchTerm]);

  // Filter appliances based on search term
  const filteredAppliances = appliances.filter(appliance =>
    appliance.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const handleSuggestionClick = (suggestion) => {
    setSearchTerm(suggestion);
    setSuggestions([]);
  };

  if (loading) {
    return <div className="text-center mt-4"><Spinner color="primary" /></div>;
  }

  return (
    <div className="mt-3">
      {/* Search Box and Refresh Button */}
      <div style={{ position: 'relative', maxWidth: '400px', margin: '0 auto' }}>
        <InputGroup className="mb-2">
          <Input
            type="text"
            placeholder="Search appliances..."
            value={searchTerm}
            onChange={e => setSearchTerm(e.target.value)}
          />
          <InputGroupText style={{ color: 'gray', marginLeft: '8px' }}>üîç</InputGroupText>
        </InputGroup>

        {/* Suggestions Dropdown */}
        {suggestions.length > 0 && (
          <ul style={{
            position: 'absolute',
            top: '100%',
            left: 0,
            right: 0,
            zIndex: 1000,
            background: '#fff',
            border: '1px solid #ccc',
            listStyle: 'none',
            padding: '5px',
            margin: 0,
            maxHeight: '150px',
            overflowY: 'auto',
          }}>
            {suggestions.map((item, idx) => (
              <li
                key={idx}
                onClick={() => handleSuggestionClick(item)}
                style={{
                  padding: '8px',
                  cursor: 'pointer',
                  borderBottom: '1px solid #eee',
                  fontSize: '14px'
                }}
              >
                {item}
              </li>
            ))}
          </ul>
        )}
      </div>

      <br />
      <br />

      <Row xs="1" sm="2" md="3" lg="4">
        {filteredAppliances.length > 0 ? (
          filteredAppliances.map((appliance) => (
            <Col key={appliance._id} className="mb-4">
              <Card className="shadow-sm h-100">
                {appliance.imgUrl && (
                  <CardImg top src={appliance.imgUrl} alt={appliance.name} style={{ height: '200px', objectFit: 'cover' }} />
                )}
                <CardBody>
                  <CardTitle tag="h5">{appliance.name}</CardTitle>
                  <CardText><strong>Price:</strong> {appliance.price}</CardText>
                  <CardText>{appliance.details}</CardText>
                  <CardText className={appliance.available ? "text-success" : "text-danger"}>
                    {appliance.available ? "Available" : "Unavailable"}
                  </CardText>
                  <Button color="primary" disabled={!appliance.available} onClick={handelBooking}>Rent</Button>
                </CardBody>
              </Card>
            </Col>
          ))
        ) : (
          <Col>
            <p className="text-center">No appliances found.</p>
          </Col>
        )}
      </Row>
    </div>
  );
};

export default ApplianceCards;
